function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>767.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const subClick=document.querySelector(".sub__click"),subMenu=document.querySelector(".sub__menu"),sub=document.querySelector(".sub");subClick.addEventListener("click",(()=>{sub.classList.toggle("active"),subMenu.classList.toggle("active")})),$(".tool__favorite").click((function(){$(this).toggleClass("choosed")})),$(".tool__add").click((function(){$(this).toggleClass("choosed")})),$(".new__favorite").click((function(){$(this).toggleClass("choosed")})),$(".new__add").click((function(){$(this).toggleClass("choosed")}));const swiper=new Swiper(".swiper",{loop:!1,pagination:{el:".swiper-pagination"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});